'use strict';

/**
 * @ngdoc directive
 * @name keddoApp.directive:admAddWidget
 * @description
 * # admAddWidget
 */
angular.module('frontendApp')
  .directive('admWidget', function () {


    //model = something;
    //hint  = something;
    return {
      scope:{item:'=item', type:'@itemType'},
      replace:true,
      templateUrl: 'editable/adm/createElementForm.html',
      controller: ["$scope","blocksFactory","fileRes","$document",function($scope,blocksFactory,fileRes,$document){


      $scope.cropStop = function(data) {
            //$scope.fileOpts = data;
            var originalImage = $scope.filePreview.thumb;
            $scope.previewData = $scope.cropImage(data);
      } 
        
      $scope.previewData = false;

      $scope.resizeImage = function(dir) {
            var originalImage = $scope.previewData;
            var img = new Image;
                img.src = originalImage;
            var canvas = document.createElement('canvas'),
                ctx = canvas.getContext('2d');
            var xfrom = 0; //0;
            var xto = img.width;

            var yfrom = 0;
            var yto = img.height;
            var coef = 1+(0.2*dir);
            canvas.width = img.width*coef;
            canvas.height = img.height*coef
            // draw source image into the off-screen canvas:
            //ctx.drawImage(img, 1100, 0, img.width, img.height);
            ctx.drawImage(img,xfrom,yfrom,xto,yto,0,0,xto*coef,yto*coef);

            // encode image to data-uri with base64 version of compressed image
            var result  =  canvas.toDataURL();
            $('#foo').attr('src',result);
            
            $scope.previewData = result;
            //$scope.filePreview.response.thumb = result;
            return result;
            
      }
        


      $scope.cropImage = function (d) {
            var originalImage = $scope.filePreview.thumb;
            var img = new Image;
                img.src = originalImage;

            var canvas = document.createElement('canvas'),
                ctx = canvas.getContext('2d');

            var xfrom = d.x; //0;
            var xto = d.w;

            var yfrom = d.y;
            var yto = d.h;

            canvas.width = d.w;
            canvas.height = d.h
            // draw source image into the off-screen canvas:
            //ctx.drawImage(img, 1100, 0, img.width, img.height);
            ctx.drawImage(img,xfrom,yfrom,xto,yto,0,0,xto,yto);

            // encode image to data-uri with base64 version of compressed image
            var result  =  canvas.toDataURL();
            $scope.previewData = result;
            $('#foo').attr('src',result);
            $scope.$apply(); 
            //$scope.filePreview.response.thumb = result;
            return result;
        }

      $scope.saveFile = function() {
            var u = window.RESTurl+'/api/files';
            $scope.file._file = str2blob();
            $scope.file.$upload(u, {}).then(function(d){ $scope.getStaticFiles();},function(d){});

      }

      $scope.file = {} //Модель
      $scope.filePreview = {}
      //$scope.saveFiles = function() {

      //}

      function str2blob(){
        var base64str = $scope.previewData.split(',')[1];
        var type = $scope.previewData.split(',')[0].match(/\w+\/\w+/g);
        //var base64str = "";
        var binary = atob(base64str.replace(/\s/g, ''));
        var len = binary.length;
        // create arraybuffer with binary length
        var buffer = new ArrayBuffer(len);
        // create 8-bit array
        var view = new Uint8Array(buffer);
        // save unicode of binary data into 8-bit array
        for (var i = 0; i < len; i++) {
         view[i] = binary.charCodeAt(i);
        }
// create the blob object with content-type "application/pdf"               
        var blob = new Blob( [view], { type: type });
        return blob;
}

      $scope.options = {
        change: function (file) {
            $scope.file = file;
            var u = window.RESTurl+'/api/files';
            file.$preview($scope.filePreview   ).then(function(d) { /*$scope.filePreview = d;*/ console.log($scope.filePreview); });
          }
        }

      $scope.getStaticFiles = function(){
        fileRes.get({}).$promise.then(function(data){ $scope.staticFiles = data.data; });
      };

        $scope.addBlock = function(i){
          var i_local = {}
          i_local.data = {};
          i_local.subitems = [];
          i_local.type = i.type;
          for (var d in i.data){
             i_local.data[d] = i.data[d];
          }
          blocksFactory.push(i_local);
        }

      }]

      }
  });
